mapper.select:
		SELECT *  FROM manager_performmance 
		WHERE manager_id=#{managerId}
		and trunc(CRATEDAY)=trunc(sysdate)

SELECT nvl(sum(ma.MONEY), 0)AS money,sum (p.VISITCOUNT) AS VISITCOUNT_S,sum (p.APPLYCOUNT) AS APPLYCOUNT_S, sum (p.APPLYREFUSE) AS APPLYREFUSE_S, sum (p.CREDITCOUNT) AS CREDITCOUNT_S, sum (p.CREDITREFUSE) AS CREDITREFUSE_S, sum (p.REALYCOUNT) AS REALYCOUNT_S, sum (p.REPORTCOUNT) AS REPORTCOUNT_S, sum (p.INTERNALCOUNT) AS INTERNALCOUNT_S, sum (p.MEETINGCOUT) AS MEETINGCOUT_S,sum (p.PASSCOUNT) AS PASSCOUNT_S, sum (p.SIGNCOUNT) AS SIGNCOUNT_S, sum (p.GIVEMONEYCOUNT) AS GIVEMONEYCOUNT_S FROM MANAGER_PERFORMMANCE p left JOIN BASIC_CUSTOMER_INFORMATION bci ON p.MANAGER_ID = bci.USER_ID left JOIN t_MIBUSIDATA_VIEW ma ON ma.CUSTID=bci.TY_CUSTOMER_ID left JOIN SYS_USER su ON su.ID=bci.USER_ID where p.manager_id = #{managerId}
and p.crateday >= to_date(#{startDate},'yyyy-mm-dd hh24:mi:ss')
AND to_date(#{endDate},'yyyy-mm-dd hh24:mi:ss')>= p.crateday

    SELECT *  FROM manager_performmance 
		WHERE manager_id=#{managerId}
		and trunc(#{reportDate})=trunc(crateday)

SELECT nvl(sum(ma.MONEY), 0)AS money,s.MANAGER_ID,sum(s.VISITCOUNT) AS VISITCOUNT, sum(s.APPLYCOUNT) AS APPLYCOUNT, sum(s.APPLYREFUSE) AS APPLYREFUSE, sum(s.CREDITCOUNT) AS CREDITCOUNT, sum(s.CREDITREFUSE) AS CREDITREFUSE, sum(s.REALYCOUNT) AS REALYCOUNT,sum(s.REPORTCOUNT) AS REPORTCOUNT, sum(s.INTERNALCOUNT) AS INTERNALCOUNT, sum(s.MEETINGCOUT) AS MEETINGCOUT, sum(s.PASSCOUNT) AS PASSCOUNT, sum(s.SIGNCOUNT) AS SIGNCOUNT, sum(s.GIVEMONEYCOUNT) AS GIVEMONEYCOUNT FROM MANAGER_PERFORMMANCE s left JOIN BASIC_CUSTOMER_INFORMATION bci ON s.MANAGER_ID = bci.USER_ID left JOIN t_MIBUSIDATA_VIEW ma ON ma.CUSTID=bci.TY_CUSTOMER_ID left JOIN SYS_USER su ON su.ID=bci.USER_ID where s.manager_id IN (select t.ID from sys_user t left join sys_dept_user sd on t.id = sd.user_id left join sys_department d on sd.dept_id = d.id left join sys_organization o on d.org_id = o.id where 1=1
and d.org_id=#{orgId}
)
and s.crateday >= to_date(#{startDate},'yyyy-mm-dd hh24:mi:ss')
AND to_date(#{endDate},'yyyy-mm-dd hh24:mi:ss')>= s.crateday
GROUP BY s.MANAGER_ID ORDER BY ${classes} DESC

select t.*,nvl(t.money,0)as money1, nvl(t1.VISITCOUNT,0) as visitcount_s, nvl(t1.APPLYCOUNT,0) as applycount_s, nvl(t1.APPLYREFUSE,0) as applyrefuse_s, nvl(t1.CREDITCOUNT,0) as creditcount_s, nvl(t1.CREDITREFUSE,0) as creditrefuse_s, nvl(t1.REALYCOUNT,0) as realycount_s, nvl(t1.REPORTCOUNT,0) as reportcount_s, nvl(t1.INTERNALCOUNT,0) as internalcount_s, nvl(t1.MEETINGCOUT,0) as meetingcout_s, nvl(t1.PASSCOUNT,0) as passcount_s, nvl(t1.SIGNCOUNT,0) as signcount_s, nvl(t1.GIVEMONEYCOUNT,0) as givemoneycount_s, s.display_name as name from MANAGER_PERFORMMANCE1 t left join (select distinct m.* from MANAGER_PERFORMMANCE m left join MANAGER_PERFORMMANCE m1 on m.manager_id=m1.manager_id where m.crateday=#{crateday}) t1 on (t.manager_id=t1.manager_id) left join sys_user s on (s.id=t.manager_id) where 1=1
and t.team=#{team}
and t.ordteam=#{ordteam}
and t.manager_id=#{manager_id}

select sum(nvl(t.VISITCOUNT,0))as visitcount,sum(nvl(t.applycount,0))as applycount,sum(nvl(t.applyrefuse,0))as applyrefuse,sum(nvl(t.creditcount,0))as creditcount,sum(nvl(t.creditrefuse,0))as creditrefuse, sum(nvl(t.realycount,0))as realycount,sum(nvl(t.reportcount,0))as reportcount,sum(nvl(t.internalcount,0))as internalcount,sum(nvl(t.meetingcout,0))as meetingcout,sum(nvl(t.passcount,0))as passcount, sum(nvl(t.signcount,0))as signcount,sum(nvl(t.givemoneycount,0))as givemoneycount,sum(nvl(t.money,0))as money1, sum(nvl(t1.VISITCOUNT,0))as visitcount_s,sum(nvl(t1.applycount,0))as applycount_s,sum(nvl(t1.applyrefuse,0))as applyrefuse_s,sum(nvl(t1.creditcount,0))as creditcount_s,sum(nvl(t1.creditrefuse,0))as creditrefuse_s, sum(nvl(t1.realycount,0))as realycount_s,sum(nvl(t1.reportcount,0))as reportcount_s,sum(nvl(t1.internalcount,0))as internalcount_s,sum(nvl(t1.meetingcout,0))as meetingcout_s,sum(nvl(t1.passcount,0))as passcount_s, sum(nvl(t1.signcount,0))as signcount_s,sum(nvl(t1.givemoneycount,0))as givemoneycount_s from MANAGER_PERFORMMANCE1 t left join (select m.* from MANAGER_PERFORMMANCE m left join MANAGER_PERFORMMANCE m1 on m.manager_id=m1.manager_id where m.crateday=#{crateday}) t1 on t.manager_id=t1.manager_id where 1=1
and t.team=#{team}
and t.ordteam=#{ordteam}

select o.name as ordteam,s.id as manager_id ,t.name as team from sys_organization o,sys_department t,sys_dept_user d,sys_user s where o.id=t.org_id and t.id=d.dept_id and d.user_id=s.id
and t.name=#{name}
and t.ordteam=#{ordteam}
group by o.name,s.id,t.name
